  } @else {
    @if nth($values, 2) { #{$right}: nth($values, 2); }
  }

  @if length($values) == 2 {
    @if nth($values, 1) { #{$bottom}: nth($values, 1); }
    @if nth($values, 2) { #{$left}:   nth($values, 2); }
